import React from 'react';
import { Modal, Button, TextField } from '@mui/material';

const CreateRecipeModal = ({ open, handleClose }) => {
    return (
        <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="create-recipe-modal"
            aria-describedby="create-recipe-form"
        >
            <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', backgroundColor: 'white', padding: '20px' }}>
                <h2 id="create-recipe-modal">Create a Recipe</h2>
                <form id="create-recipe-form">
                    <TextField label="Title" fullWidth margin="normal" />
                    <TextField label="Description" fullWidth multiline rows={4} margin="normal" />
                    <TextField label="Ingredients" fullWidth multiline rows={4} margin="normal" />
                    <TextField label="Instructions" fullWidth multiline rows={6} margin="normal" />
                    <Button variant="contained" color="primary" style={{ marginTop: '20px' }}>Create</Button>
                </form>
            </div>
        </Modal>
    );
};

export default CreateRecipeModal;
