import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Typography } from '@mui/material';

const FullRecipe = ({ recipe, isAuthenticated }) => {
    const history = useNavigate();

    const handleLike = async () => {
        try {
            const response = await fetch(`${process.env.REACT_APP_BACKEND_API}/api/recipes/${recipe._id}/like`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            if (!response.ok) {
                throw new Error('Failed to like recipe');
            }
            console.log('Recipe liked');
        } catch (error) {
            console.error('Error liking recipe:', error);
        }
    };

    const handleSave = async () => {
        try {
            const response = await fetch(`${process.env.REACT_APP_BACKEND_API}/api/recipes/${recipe._id}/save`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            if (!response.ok) {
                throw new Error('Failed to save recipe');
            }
            console.log('Recipe saved');
        } catch (error) {
            console.error('Error saving recipe:', error);
        }
    };

    return (
        <div>
            <Typography variant="h4">{recipe.title}</Typography>
            <Typography variant="body1">{recipe.description}</Typography>
            <Typography variant="h6">Ingredients</Typography>
            <ul>
                {recipe.ingredients.map((ingredient, index) => (
                    <li key={index}>{ingredient}</li>
                ))}
            </ul>
            <Typography variant="h6">Instructions</Typography>
            <Typography variant="body1">{recipe.instructions}</Typography>
            {isAuthenticated ? (
                <div>
                    <Button variant="contained" color="primary" onClick={handleLike}>Like</Button>
                    <Button variant="contained" color="primary" onClick={handleSave}>Save</Button>
                </div>
            ) : (
                <Typography variant="body1">You need to <Button onClick={() => history.push('/login')}>log in</Button> to like or save this recipe.</Typography>
            )}
        </div>
    );
};

export default FullRecipe;
