const CreateRecipeModal = ({ open, handleClose }) => {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        ingredients: '',
        instructions: '',
        image: null // Initialiser l'image à null
    });

    const handleChange = (e) => {
        if (e.target.name === 'image') {
            const file = e.target.files[0]; // Récupérer le fichier d'image
            setFormData({ ...formData, image: file }); // Mettre à jour l'état avec le fichier d'image
        } else {
            const { name, value } = e.target;
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async () => {
        const formDataWithImage = new FormData(); // Créer un nouvel objet FormData

        // Ajouter chaque champ du formulaire à l'objet FormData
        for (const key in formData) {
            formDataWithImage.append(key, formData[key]);
        }

        console.log('Form data with image:', formDataWithImage);

        try {
            const response = await fetch(`${process.env.REACT_APP_BACKEND_API}/api/recipes`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: formDataWithImage // Envoyer l'objet FormData contenant les données du formulaire et l'image
            });

            if (!response.ok) {
                throw new Error('Failed to create recipe');
            }

            const data = await response.json();
            console.log('Recipe created:', data);
            handleClose(); // Fermer le modal après la création de la recette
        } catch (error) {
            console.error('Error creating recipe:', error);
            // Gérer les erreurs lors de la création de la recette
        }
    };

    return (
        <div>
            <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="create-recipe-modal"
            aria-describedby="create-recipe-form"
        >
            <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', backgroundColor: 'white', padding: '20px' }}>
                <h2 id="create-recipe-modal">Create a Recipe</h2>
                <form id="create-recipe-form">
                    <TextField
                        label="Title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        fullWidth
                        multiline
                        rows={4}
                        margin="normal"
                    />
                    <TextField
                        label="Ingredients"
                        name="ingredients"
                        value={formData.ingredients}
                        onChange={handleChange}
                        fullWidth
                        multiline
                        rows={4}
                        margin="normal"
                    />
                    <TextField
                        label="Instructions"
                        name="instructions"
                        value={formData.instructions}
                        onChange={handleChange}
                        fullWidth
                        multiline
                        rows={6}
                        margin="normal"
                    />
                    <input
                        type="file"
                        accept="image/*"
                        name="image"
                        onChange={handleChange}
                        style={{ margin: '20px 0' }}
                    />
                    <Button variant="contained" color="primary" style={{ marginTop: '20px' }} onClick={handleSubmit}>Create</Button>
                </form>
            </div>
        </Modal>
        </div>
    );
};
