import React from 'react';
import { Link } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Menu,
  MenuItem,
} from '@mui/material';
import { styled } from '@mui/material/styles';

const StyledToolbar = styled(Toolbar)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'space-between', // Distribute content evenly
  alignItems: 'center', // Center elements vertically
  padding: theme.spacing(1), // Add some padding

  [theme.breakpoints.down('sm')]: { // Responsive adjustments for smaller screens
    justifyContent: 'flex-start', // Align content to the left
  },
}));

const Navbar = ({ isAuthenticated, isAdmin }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar position="static" color="primary">
      <StyledToolbar>
        <Typography variant="h6" component="div" sx={{ color: 'white' }}>
          Mon Site
        </Typography>
        <div>
          <Button color="inherit" component={Link} to="/">Accueil</Button>
          <Button color="inherit" component={Link} to="/profile">Profil</Button>
          <Button color="inherit" component={Link} to="/search">Recherche</Button>
        </div>
        {isAdmin && (
          <IconButton
            aria-label="account of current user"
            aria-controls={open ? 'menu-appbar' : undefined}
            aria-haspopup="true"
            onClick={handleMenu}
            color="inherit"
          >
            {/* Add a suitable icon here (e.g., AccountCircle) */}
          </IconButton>
        )}
        {!isAuthenticated && (
          <>
            <Button color="inherit" component={Link} to="/login">Connexion</Button>
            <Button color="inherit" component={Link} to="/register">Inscription</Button>
          </>
        )}
        <Menu
          id="menu-appbar"
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          open={open}
          onClose={handleClose}
          sx={{
            '& .MuiList-root': {
              bgcolor: 'primary.main', // Match primary color
            },
          }}
        >
          <MenuItem onClick={handleClose} component={Link} to="/admin">
            Admin
          </MenuItem>
        </Menu>
      </StyledToolbar>
    </AppBar>
  );
};

export default Navbar;
