import React, { useState } from 'react';
import { Modal, Button, TextField } from '@mui/material';

const CreateRecipeModal = ({ open, handleClose }) => {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        ingredients: '',
        instructions: '',
        image: '' // Ajoutez un champ pour l'image
    });

    const handleChange = (e) => {
        if (e.target.name === 'image') {
            // Si le changement concerne le champ de l'image
            const file = e.target.files[0]; // Récupérez le fichier
            setFormData({ ...formData, image: file }); // Mettez à jour l'état avec le fichier
        } else {
            // Si le changement concerne d'autres champs du formulaire
            const { name, value } = e.target;
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async () => {
        // Envoyez les données du formulaire y compris l'image
        const formDataWithImage = new FormData();
        for (const key in formData) {
            formDataWithImage.append(key, formData[key]);
        }

        await fetch(`${process.env.REACT_APP_BACKEND_API}/api/recipes`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}` // Ajoutez le token JWT stocké dans localStorage
            },
            body: formDataWithImage // Envoyez le FormData contenant les données du formulaire y compris l'image
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to create recipe');
            }
            return response.json();
        })
        .then(data => {
            console.log('Recipe created:', data);
            handleClose(); // Fermez le modal après la création de la recette
        })
        .catch(error => {
            console.error('Error creating recipe:', error);
            // Gérer les erreurs lors de la création de la recette
        });
    };

    return (
        <div>
            <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="create-recipe-modal"
            aria-describedby="create-recipe-form"
        >
            <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', backgroundColor: 'white', padding: '20px' }}>
                <h2 id="create-recipe-modal">Create a Recipe</h2>
                <form id="create-recipe-form">
                    <TextField
                        label="Title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        fullWidth
                        multiline
                        rows={4}
                        margin="normal"
                    />
                    <TextField
                        label="Ingredients"
                        name="ingredients"
                        value={formData.ingredients}
                        onChange={handleChange}
                        fullWidth
                        multiline
                        rows={4}
                        margin="normal"
                    />
                    <TextField
                        label="Instructions"
                        name="instructions"
                        value={formData.instructions}
                        onChange={handleChange}
                        fullWidth
                        multiline
                        rows={6}
                        margin="normal"
                    />
                    <TextField
                        type="file"
                        label="Image"
                        name="image"
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <Button variant="contained" color="primary" style={{ marginTop: '20px' }} onClick={handleSubmit}>Create</Button>
                </form>
            </div>
        </Modal>
        </div>

    );
};

export default CreateRecipeModal;
