// RecipeCard.jsx
import React from 'react';
import { Card, CardActionArea, CardContent, Typography } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import Modal from './FullRecipe'; // Supposons que vous avez un composant Modal pour afficher la recette complète

const RecipeCard = ({ recipe, isAuthenticated, openModal }) => {
    return (
        <Card>
            <CardActionArea onClick={() => openModal(recipe)}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {recipe.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {recipe.description}
                    </Typography>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                        <Typography variant="body2" color="text.secondary">Likes: {recipe.likes}</Typography>
                        {isAuthenticated && (
                            <div>
                                <FavoriteIcon color="secondary" />
                                <BookmarkIcon color="primary" />
                            </div>
                        )}
                    </div>
                </CardContent>
            </CardActionArea>
        </Card>
    );
};

export default RecipeCard;
