import React, { useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Grid, Typography, Avatar, List, ListItem, ListItemText, Paper } from '@mui/material';
import { useAuth } from '../AuthContext';
import Navbar from './Navbar';

const ProfilePage = () => {
    const { isAuthenticated, isAdmin} = useAuth();
    const user = {
        username: 'NomUtilisateur',
        email: 'email@example.com',
        profilePicture: 'https://via.placeholder.com/150' // Remplacez par l'URL réelle de l'image de profil
    };
    const navigate = useNavigate(); // Initialise le hook useHistory

    useEffect(() => {
        console.log("okook",isAuthenticated);
        if (!isAuthenticated) {
          navigate('/');
        }
      }, [isAuthenticated, navigate]);

    if (!isAuthenticated) {
        return <Typography variant="h6" align="center">Vous devez être connecté pour voir cette page.</Typography>;
    }

    return (
        <Container maxWidth="md" style={{ marginTop: '50px' }}>
            <Navbar isAuthenticated={isAuthenticated} isAdmin={isAdmin} /> {/* Barre de navigation */}
            <Grid container spacing={3}>
                <Grid item xs={12} sm={4}>
                    <Paper elevation={3} style={{ padding: '20px', textAlign: 'center' }}>
                        <Avatar
                            alt={user.username}
                            src={user.profilePicture}
                            sx={{ width: 150, height: 150, margin: 'auto' }}
                        />
                        <Typography variant="h6" style={{ marginTop: '20px' }}>{user.username}</Typography>
                        <Typography variant="body1">{user.email}</Typography>
                    </Paper>
                </Grid>
                <Grid item xs={12} sm={8}>
                    <Paper elevation={3} style={{ padding: '20px' }}>
                        <Typography variant="h6" style={{ marginBottom: '20px' }}>Menu</Typography>
                        <List>
                            <ListItem button component={Link} to="/create-recipe">
                                <ListItemText primary="Créer une recette" />
                            </ListItem>
                            <ListItem button component={Link} to="/liked-posts">
                                <ListItemText primary="Posts likés" />
                            </ListItem>
                            <ListItem button component={Link} to="/saved-posts">
                                <ListItemText primary="Posts enregistrés" />
                            </ListItem>
                        </List>
                    </Paper>
                </Grid>
            </Grid>
        </Container>
    );
};

export default ProfilePage;
