import React from 'react';
import { Link } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Button, IconButton, Menu, MenuItem } from '@mui/material';
import { useAuth } from '../AuthContext'; // Importez le hook useAuth depuis votre contexte d'authentification

const Navbar = ({ isAuthenticated, isAdmin }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
    const { setIsAuthenticated, setIsAdmin } = useAuth(); // Utilisez le hook useAuth pour accéder à setIsAuthenticated et setIsAdmin

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = async () => {
-        setIsAuthenticated(false); // Example for local state with useState
-        setIsAdmin(false); // Example for local state with useState
  };

  return (
    <AppBar position="static" color="primary">
      <Toolbar>
        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          Mon Site
        </Typography>
        <div>
          <Button color="inherit" component={Link} to="/">Accueil</Button>
          <Button color="inherit" component={Link} to="/profile">Profil</Button>
          <Button color="inherit" component={Link} to="/search">Recherche</Button>
          {isAuthenticated && <Button color="inherit" onClick={handleLogout}>Déconnexion</Button>}
        </div>
        {isAdmin && (
          <IconButton
            aria-label="account of current user"
            aria-controls={open ? 'menu-appbar' : undefined}
            aria-haspopup="true"
            onClick={handleMenu}
            color="inherit"
          >
            {/* Add a suitable icon here (e.g., AccountCircle) */}
          </IconButton>
        )}
        {!isAuthenticated && (
          <>
            <Button color="inherit" component={Link} to="/login">Connexion</Button>
            <Button color="inherit" component={Link} to="/register">Inscription</Button>
          </>
        )}
        <Menu
          id="menu-appbar"
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          open={open}
          onClose={handleClose}
        >
          <MenuItem onClick={handleClose} component={Link} to="/admin">
            Admin
          </MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
